import React from 'react';
import { Link } from 'react-router-dom';
import  {auth} from '../../firebase/firebase.utils'
import { ReactComponent as Logo } from '../../assets/logo/crown.svg';
import {connect} from 'react-redux'
import './header.styles.scss';

// if currentUser state is changed, re-render
const Header = ({currentUser}) => (
  <div className='header'>
    <Link className='logo-container' to='/'>
      <Logo className='logo' />
    </Link>
    <div className='options'>
      <Link className='option' to='/shop'>
        SHOP
      </Link>
      <Link className='option' to='/shop'>
        CONTACT
      </Link>
        {currentUser ?
          <div className="option" onClick={() => auth.signOut}>SIGN OUT</div>
          :
          <Link className="option" to='/signin'>SIGN IN</Link>
        }
    </div>
  </div>
);

// catches the updated state and pulls out current user ready to pass it into Header
const mapStateToProps = (state) => ({
  currentUser: state.user.currentUser
})

// whenever redux store changes, mapStateToPropsCalled
// and forwards the specific currentUser prop into Header as if normal prop
export default connect(mapStateToProps)(Header);
