    <CartItemContainer>
        <img src={imageUrl} alt="item" />
        <ItemDetails>
            <ItemName>{name}</ItemName>
            <ItemCost>Quantity: {quantity} | Â£{(Math.round(price*quantity * 100) / 100).toFixed(2)}</ItemCost>
        </ItemDetails>
    </CartItemContainer>
);

export default CartItem;
