import styled, { css } from "styled-components";

const isEmpty = ({length}) => (
    length ? '' : css`overflow-y: hidden`
)

export const CartDropdownContainer = styled.div`
    position: absolute;
	width: 295px;
	// height: 400px;
	height: fit-content;
	display: flex;
	flex-direction: column;
	padding: 20px;
	border: 1px solid black;
	background-color: white;
	top: 90px;
	right: 86px;
	z-index: 5;
`

export const EmptyMessage = styled.span`
    font-size: 18px;
    text-align: center;
    padding: 15px;
    padding-top: 5px;
`
export const CartItems = styled.div`
    max-height: 295px;
    display: flex;
    flex-direction: column;
    overflow-y: scroll;

    ${isEmpty}
`
