import React from 'react'

import './checkout-item.styles.scss'
import { removeProduct } from '../../redux/cart/cart.actions';
import { connect } from 'react-redux';
import { addItemToCart } from '../../redux/cart/cart.utils';

const CheckoutItem = ({ cartItem, removeProduct, addItem }) => {
    const { name, imageUrl, price, quantity } = cartItem
    return (
    <div className="checkout-item">
        <div className="image-container">
            <img src={imageUrl} alt="item" />
        </div>
        <span className="name">{name}</span>
            <span className="quantity">
                <div onClick={ ()=>removeProduct(cartItem)} className="arrow">&#10094;</div>
                {quantity}
                <div onClick={()=>addItem(cartItem) } className="arrow">&#10095;</div>
            </span>
        <span className="price">{price}</span>
        <div onClick={() => removeProduct(cartItem)}className="remove-button">&#10005;</div>
    </div>
    )
}

const mapDispatchToProps = (dispatch) => ({
    removeProduct: (item) => dispatch(removeProduct(item)),
    addItem: (item) => dispatch(addItemToCart(item))
})

export default connect(null, mapDispatchToProps)(CheckoutItem)
