import SHOP_DATA from "../../redux/data/shop.data";
import { getShopData } from "./shop.utils";

const INITIAL_STATE = {
	collections: getShopData().data(),
};

const shopReducer = (state = INITIAL_STATE, action) => {
	switch (action.type) {
		default:
			return state;
	}
};

export default shopReducer;
